import { Component, OnInit } from "@angular/core";
import { Router } from "@angular/router";
import { DataSharingService } from "../../../services/data-sharing.services";
import { Product } from "../../../services/globals";
import { CartService } from "../../../services/cart/cart.service";
import { CategoryService } from "src/app/services/category/category.service";

@Component({
  selector: "app-home",
  templateUrl: "./home.page.html",
  styleUrls: ["./home.page.scss"],
})
export class HomePage implements OnInit {
  constructor(
    private route: Router,
    private dataSharingService: DataSharingService,
    private cartService: CartService,
    public categoryServices: CategoryService
  ) {}

  public segment: string = "vegetables";

  ngOnInit() {
    // this.products.quantity = 0;
  }

  products: Product[] = [
    {
      productId: 1,
      name: "Broccoli",
      variant: "750gm",
      price: 90.0,
      oldPrice: 100,
      quantity: 0,
    },
    {
      productId: 2,
      name: "Orange",
      variant: "1kg",
      price: 54.0,
      oldPrice: 60,
      quantity: 0,
    },
    {
      productId: 3,
      name: "Milk",
      variant: "1 Litre",
      price: 72.0,
      oldPrice: 80,
      quantity: 0,
    },
    {
      productId: 4,
      name: "Tomato",
      variant: "1kg",
      price: 30.0,
      oldPrice: 34,
      quantity: 0,
    },
  ];

  addToCart(product: Product) {
    this.cartService.addToCart(product);
  }

  removeFromCart(product: Product) {
    //console.log("Removing from cart", product);
    this.cartService.removeFromCart(product.productId);
  }


  listGreenVeggies() {
    this.route.navigate(["/tabs/category"]);
    this.categoryServices.segmentChanged("vegetables");
    //console.log("clicked veggie");
  }

  listFruits() {
    this.route.navigate(["/tabs/category"]);
    this.categoryServices.segmentChanged("fruits");
  }

  listDairyProducts() {
    this.route.navigate(["/tabs/category"]);
    this.categoryServices.segmentChanged("dairy");
  }

  listFruitsVeggies() {
    this.route.navigate(["/tabs/category"]);
    this.categoryServices.segmentChanged("vegetables");
  }

  listBakeryItems() {
    this.route.navigate(["/tabs/category"]);
    this.categoryServices.segmentChanged("bakery");
  }

  listFineDairyProducts() {
    this.route.navigate(["/tabs/category"]);
    this.categoryServices.segmentChanged("dairy");
  }
}
<ion-header [translucent]="true">
  <ion-toolbar>
    <ion-buttons slot="start">
      <ion-menu-button></ion-menu-button>
    </ion-buttons>
    <ion-title>Home</ion-title>
  </ion-toolbar>
</ion-header>

<ion-content [fullscreen]="true">
  <div class="ion-padding">
    <div class="dailyDeals">
      <h1>Today's Best Deal</h1>
    </div>
    <div class="dailyDealList">
      <div class="productWrapper" *ngFor="let product of products">
        <div class="ion-text-center">
          <img
            src="../../assets/img/{{ product.name.toLowerCase() }}.png"
            alt="{{ product.name }}"
          />
          <span>10% Off</span>
        </div>
        <div>
          <div class="productName">{{ product.name }}</div>
          <div class="productVariant">{{ product.variant }}</div>
        </div>
        <div>
          <div class="productActionRow">
            <div class="productPrice">
              $ {{ product.price.toFixed(2) }}
              <div class="oldPrice">$ {{ product.oldPrice }}</div>
            </div>
            <ion-button (click)="addToCart(product)">+ </ion-button>
            {{ product.quantity }} <i class=""></i>
            <ion-button (click)="removeFromCart(product)"> - </ion-button>
          </div>
        </div>
      </div>
    </div>
  </div>

  <!--category-->
  <div class="ion-padding">
    <div class="dailyDeals">
      <h1>Shop by Category</h1>
    </div>

    <div class="productCategory">
      <div (click)="listGreenVeggies()" >
        <img src="../../assets/img/broccoli.png" alt="image" />
        <h2>Green Veggies</h2>
      </div>
      <div (click)="listFruits()">
        <img src="../../assets/img/orange.png" alt="image" />
        <h2>Fruits</h2>
      </div>
      <div (click)="listDairyProducts()">
        <img src="../../assets/img/milk.png" alt="image" />
        <h2>Dairy</h2>
      </div>
      <div (click)="listFruitsVeggies()">
        <img src="../../assets/img/tomato.png" alt="image" />
        <h2>Vegetables</h2>
      </div>
      <div (click)="listBakeryItems()">
        <img src="../../assets/img/image-5.png" alt="image" />
        <h2>Bakery</h2>
      </div>
      <div (click)="listFineDairyProducts()">
        <img src="../../assets/img/milk.png" alt="image" />
        <h2>Fine Dairy Products</h2>
      </div>
    </div>
  </div>
</ion-content>
