import { Component, OnInit } from "@angular/core";
import { Router } from "@angular/router";
import { CartService } from "../../../services/cart/cart.service";
import { Product } from "../../../services/globals";
import { OrderService } from "../../../services/order/order.service";

@Component({
  selector: "app-cart",
  templateUrl: "./cart.page.html",
  styleUrls: ["./cart.page.scss"],
})
export class CartPage implements OnInit {
  cartItems: Array<Product> = [];

  constructor(public cartService: CartService, public router: Router,private orderServices:OrderService) {
    this.cartItems = this.cartService.getCartItems();
  }

  ngOnInit() {}

  redirectToCheckout() {
    this.router.navigate(["/tabs/onboarding"]);
    //console.log("Redirecting to checkout page");
  }
}

<!-- cart.component.html -->
<ion-content [fullscreen]="true">
  <div class="ion-padding">
    <div class="cartHeading">
      <h1>Item List</h1>
      <span>{{ cartItems.length }} Item</span>
    </div>
    <div class="cartProductList">
      <div class="cartProductWrapper" *ngFor="let product of cartItems">
        <div>
          <img
            src="../../assets/img/{{ product.name.toLowerCase() }}.png"
            alt="{{ product.name }}"
          />
        </div>
        <div>
          <div class="productPrice">${{ product.price.toFixed(2) }}</div>
          <div class="productName">{{ product.name }}</div>
          <div class="productVariant">{{ product.variant }}</div>
          <div class="cartQtyAmount">
            <ion-button (click)="cartService.addToCart(product)">+ </ion-button>
            {{ product.quantity }} <i class=""></i>
            <ion-button (click)="cartService.removeFromCart(product.productId)"> - </ion-button>
          </div>
        </div>
      </div>
    </div>
  </div>
</ion-content>
<ion-footer class="ion-padding">
  <ion-button expand="block" (click)="redirectToCheckout()" [disabled]="cartItems.length === 0"
    >Proceed to checkout (${{ this.cartService.totalCartValue }})</ion-button
  >
</ion-footer>
