import { Component } from "@angular/core";
import { FormBuilder, FormGroup, Validators } from "@angular/forms";
import { Router } from "@angular/router";
import { DataSharingService } from "../../../services/data-sharing.services";
import { Globals } from "../../../services/globals";

@Component({
  selector: "app-login",
  templateUrl: "./login.page.html",
  styleUrls: ["./login.page.scss"],
})
export class LoginPage {
  loginForm!: FormGroup;
  forDataTobeSendForAdminRegistration: any = {};

  constructor(
    private router: Router,
    private fb: FormBuilder,
    public dataSharingService: DataSharingService,
    public globals: Globals
  ) {
    this.globals.authenticated = false;
    this.initializeLoginForm();
  }

  private initializeLoginForm(): void {
    this.loginForm = this.fb.group({
      userType: ["", [Validators.required]],
      name: ["", [Validators.required, Validators.pattern("^[a-zA-Z ]+$")]],
      mobileNumber: [
        "",
        [Validators.required, Validators.pattern("^[0-9]{10}$")],
      ],
      city: ["", [Validators.required]],
      area: ["", [Validators.required]],
      building: ["", [Validators.required]],
      flatNumber: ["", Validators.required],
      floor: ["", Validators.required]
    });

    this.updateValidators();
  }

  private updateValidators(): void {
    const nameControl = this.loginForm.get("name");
    const mobileNumberControl = this.loginForm.get("mobileNumber");
    const cityControl = this.loginForm.get("city");
    const areaControl = this.loginForm.get("area");
    const buildingControl = this.loginForm.get("building");
    const flatNumberControl = this.loginForm.get("flatNumber");
    const floorControl = this.loginForm.get("floor");


    if (this.dataSharingService.loginMode) {
      nameControl?.clearValidators();
      cityControl?.clearValidators();
      areaControl?.clearValidators();
      buildingControl?.clearValidators();
      flatNumberControl?.clearValidators();
      floorControl?.clearValidators();
      mobileNumberControl?.setValidators([
        Validators.required,
        Validators.pattern("^[0-9]{10}$"),
      ]);
    } else {
      nameControl?.setValidators([
        Validators.required,
        Validators.pattern("^[a-zA-Z ]+$"),
      ]);
      mobileNumberControl?.setValidators([
        Validators.required,
        Validators.pattern("^[0-9]{10}$"),
      ]);
    }

    nameControl?.updateValueAndValidity();
    mobileNumberControl?.updateValueAndValidity();
  }

  public loginMode(event?: Event): void {
    if (event) {
      event.preventDefault();
    }

    this.dataSharingService.loginMode = true;
    this.updateValidators();
  }

  public signUpMode(event?: Event): void {
    if (event) {
      event.preventDefault();
    }

    this.dataSharingService.loginMode = false;
    this.updateValidators();
  }

  redirectToOTP() {
    if (this.loginForm.valid) {
      const formData = this.loginForm.value;
      // if (formData.userType === "Admin") {
      //   this.forDataTobeSendForAdminRegistration = {
      //     userType: formData.userType,
      //     name: formData.name,
      //     mobileNumber: formData.mobileNumber,
      //     city: formData.city,
      //   };
      // } else {
      //   this.forDataTobeSendForAdminRegistration = formData;
      // }
      const dataToSend = this.dataSharingService.loginMode
        ? { mobileNumber: formData.mobileNumber, userType: formData.userType }
        : formData;

      this.dataSharingService.setFormData(dataToSend);
      this.router.navigate(["/otp"]);
    } else {
      Object.values(this.loginForm.controls).forEach((control) =>
        control.markAsTouched()
      );
    }
  }

  onUserTypeChange() {
    const userTypeControl = this.loginForm.get("userType");
    const userType = userTypeControl?.value;
    //console.log("userType", userType);
    const areaControl = this.loginForm.get("area");
    const buildingCOntrol = this.loginForm.get("building");

    // if (userType === "Admin") {
    //   areaControl?.clearValidators();
    //   areaControl?.updateValueAndValidity();
    //   buildingCOntrol?.clearValidators();
    //   buildingCOntrol?.updateValueAndValidity();
    // } else {
    //   areaControl?.setValidators([Validators.required]),
    //     buildingCOntrol?.setValidators([Validators.required]);
    // }
  }
}
<ion-content [fullscreen]="true">
  <div class="pageWrapper">
    <div class="topWrapper"></div>
    <div class="bottomWrapper">
      <div class="pageHeading">
        <h1>Welcome</h1>
        <p>
          <a
            href="#"
            [class.active]="this.dataSharingService.loginMode"
            (click)="loginMode($event)"
            >Login</a
          >
          or
          <a
            href="#"
            [class.active]="!this.dataSharingService.loginMode"
            (click)="signUpMode($event)"
            >Signup</a
          >
          to continue
        </p>
      </div>

      <div class="pageRow ion-padding-horizontal">
        <form [formGroup]="loginForm">
          <!-- Add name input -->
          <ion-select
            placeholder="Select User Type"
            class="ion-margin-bottom"
            formControlName="userType"
            (ionChange)="onUserTypeChange()"
          >
            <ion-select-option value="Admin">Admin</ion-select-option>
            <ion-select-option value="BuildingSuperwiser"
              >Building-Admin</ion-select-option
            >
            <ion-select-option value="Customer">Customer</ion-select-option>
          </ion-select>
          <ion-select
            *ngIf="!this.dataSharingService.loginMode"
            placeholder="Select City"
            class="ion-margin-bottom"
            formControlName="city"
          >
            <ion-select-option value="Lucknow">Lucknow</ion-select-option>
            <ion-select-option value="Kanpur">Kanpur</ion-select-option>
            <ion-select-option value="Delhi">Delhi</ion-select-option>
          </ion-select>
          <ion-select
            *ngIf="!this.dataSharingService.loginMode "
            placeholder="Select Local Area"
            class="ion-margin-bottom"
            formControlName="area"
          >
            <ion-select-option value="Sector-10">Sector-10</ion-select-option>
            <ion-select-option value="Sector-11">Sector-11</ion-select-option>
            <ion-select-option value="Sector-12">Sector-12</ion-select-option>
          </ion-select>
          <ion-select
            *ngIf="!this.dataSharingService.loginMode"
            placeholder="Select Building/Apartment"
            class="ion-margin-bottom"
            formControlName="building"
          >
            <ion-select-option value="Amaatra Homes"
              >Amaatra Homes</ion-select-option
            >
            <ion-select-option value="Sangam Homes"
              >Sangam Homes</ion-select-option
            >
            <ion-select-option value="Mahagun Mantraa"
              >Mahagun Mantraa</ion-select-option
            >
          </ion-select>

          <div
            class="ion-padding-top"
            *ngIf="!this.dataSharingService.loginMode"
          >
            <h3>Address</h3>
            <ion-input
              type="number"
              placeholder="Enter Flat/House Number"
              class="ion-margin-bottom"
              formControlName="flatNumber"
            ></ion-input>
            <ion-select placeholder="Select Floor" formControlName="floor" class="ion-margin-bottom">
              <ion-select-option value="1">1</ion-select-option>
              <ion-select-option value="2">2</ion-select-option>
              <ion-select-option value="3">3</ion-select-option>
            </ion-select>
          </div>
          <ion-input
            *ngIf="!this.dataSharingService.loginMode"
            formControlName="name"
            type="text"
            placeholder="Enter Name"
            class="ion-padding-horizontal ion-margin-bottom"
            [class.invalid]="loginForm.get('name')?.invalid && loginForm.get('name')?.touched"
          ></ion-input>
          <div
            *ngIf="loginForm.get('name')?.invalid && loginForm.get('name')?.touched"
            class="invalid-message"
          >
            Alphabetic characters and spaces allowed.
          </div>

          <ion-input
            formControlName="mobileNumber"
            type="number"
            placeholder="Enter Mobile Number"
            class="ion-padding-horizontal ion-margin-bottom"
            [class.invalid]="loginForm.get('mobileNumber')?.invalid && loginForm.get('mobileNumber')?.touched"
          ></ion-input>
          <div
            *ngIf="loginForm.get('mobileNumber')?.invalid && loginForm.get('mobileNumber')?.touched"
            class="invalid-message"
          >
            10-digit Mobile number required.
          </div>

          <ion-button
            expand="block"
            [disabled]="loginForm.invalid"
            (click)="redirectToOTP()"
            >Continue</ion-button
          >

          <div class="contidionLabel">
            By continuing you agree to our <br />
            <span
              ><a href="#">Terms of services</a
              ><a href="">Privacy Policy</a></span
            >
          </div>
        </form>
      </div>
    </div>
  </div>
</ion-content>
